CREATE OR REPLACE FUNCTION replace_special_chars(p_string IN VARCHAR2, p_representation IN VARCHAR2) RETURN VARCHAR2\nIS\n  v_result VARCHAR2(4000);\nBEGIN\n  v_result := TRANSLATE(p_string, CHR(0) || CHR(1) || ... || CHR(31), \n                        CASE p_representation\n                          WHEN 'HEX' THEN '\x00\x01\x02...' || '\x1F'\n                          WHEN 'OCT' THEN '\000\001\002...' || '\031'\n                          WHEN 'CSTYLE' THEN '\0\1\2...' || '\31'\n                          WHEN 'NULL' THEN NULL\n                          ELSE p_representation\n                        END);\n  RETURN v_result;\nEND;\n/