-- Initial value of @email\n@email = NULL\n\n-- First row\n@email = ISNULL(@email + '; ', '') + 'email1@example.com'\n@email = 'email1@example.com'\n\n-- Second row\n@email = ISNULL(@email + '; ', '') + 'email2@example.com'\n@email = 'email1@example.com; email2@example.com'\n\n-- Third row\n@email = ISNULL(@email + '; ', '') + 'email3@example.com'\n@email = 'email1@example.com; email2@example.com; email3@example.com'\n\n-- ... and so on for all 10 rows