-- Create a linked server to the remote database\nEXEC sp_addlinkedserver @server='RemoteServer', @srvproduct='SQL Server'\n\n-- Create a trigger to replicate data\nCREATE TRIGGER trg_EmployeeID_Replication\nON Employees\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    -- Replicate data to the remote database\n    INSERT INTO RemoteServer.Inventory.dbo.EmployeeReferences (EmployeeID)\n    SELECT i.EmployeeID\n    FROM inserted i\n    WHERE NOT EXISTS (\n        SELECT 1\n        FROM RemoteServer.Inventory.dbo.EmployeeReferences er\n        WHERE er.EmployeeID = i.EmployeeID\n    )\nEND