-- Create a sample table\nCREATE TABLE test_table (id NUMBER, name VARCHAR2(50));\n\n-- Insert some sample data\nINSERT INTO test_table (id, name) VALUES (1, 'John');\nINSERT INTO test_table (id, name) VALUES (2, 'Jane');\n\n-- Create a stored procedure\nCREATE OR REPLACE PROCEDURE test_proc(p_id NUMBER)\nAS\nBEGIN\n  SELECT name INTO v_name FROM test_table WHERE id = p_id;\nEND;\n\n-- Execute the stored procedure\nEXEC test_proc(1);\n\n-- Get the execution plan for the stored procedure\nSELECT * FROM TABLE(DBMS_XPLAN.DISPLAY_CURSOR());\n\n-- Execute the dynamic SQL statement\nDECLARE\n  v_name VARCHAR2(50);\nBEGIN\n  EXECUTE IMMEDIATE 'SELECT name FROM test_table WHERE id = 1' INTO v_name;\nEND;\n\n-- Get the execution plan for the dynamic SQL statement\nSELECT * FROM TABLE(DBMS_XPLAN.DISPLAY_CURSOR());