CREATE TRIGGER trg_Foo ON Foo\nAFTER INSERT, UPDATE, DELETE\nAS\nBEGIN\n    IF EXISTS (SELECT 1 FROM Inserted)\n    BEGIN\n        -- Insert operation\n        SELECT 'Insert: ' + i.Name\n        FROM Inserted i\n    END\n    ELSE IF EXISTS (SELECT 1 FROM Deleted)\n    BEGIN\n        -- Delete operation\n        SELECT 'Delete: ' + d.FooId + ' ' + d.Name\n        FROM Deleted d\n    END\n    ELSE IF EXISTS (SELECT 1 FROM Inserted) AND EXISTS (SELECT 1 FROM Deleted)\n    BEGIN\n        -- Update operation\n        SELECT 'Update: ' + i.FooId + ' ' + i.Name\n        FROM Inserted i\n        INNER JOIN Deleted d ON i.FooId = d.FooId\n    END\nEND