SELECT  bs_accts.acct_num,  \n          bs_accts.acct_name,  \n          br_data.record_id ,  \n       as_users.username,   \n       br_fx.fx_rate  \nFROM    bs_accts\nCROSS JOIN as_users  \nCROSS JOIN br_fx\nLEFT OUTER JOIN br_data  \nON (as_users.userid = br_data.last_note_user \n    AND br_fx.iso_src = br_data.user_ten\n    AND br_data.state = 3\n    AND br_data.acct_id = bs_accts.acct_id)\nWHERE   bs_accts.acct_currency = 'SHS'        \nAND     substr(bs_accts.acct_num, 1, 1) = 'T'\nAND     br_fx.iso_dst = 'USD';