CREATE TABLE TableLookup\n(\n    TableName sysname PRIMARY KEY,\n    QueryString nvarchar(max)\n)\n\nINSERT INTO TableLookup (TableName, QueryString)\nVALUES ('TableA', 'SELECT * FROM [dbo].[TableA]'),\n       ('TableB', 'SELECT * FROM [dbo].[TableB]')\n\nCREATE PROCEDURE GetTableData (@TableName sysname)\nAS\nBEGIN\n    DECLARE @QueryString nvarchar(max)\n    SET @QueryString = (SELECT QueryString FROM TableLookup WHERE TableName = @TableName)\n    EXEC sp_executesql @QueryString\nEND