-- Create a new database with a secure connection\nCREATE DATABASE [MyDatabase]\nON PRIMARY (\n    NAME = N'MyDatabase',\n    FILENAME = N'C:\Databases\MyDatabase.mdf',\n    SIZE = 10MB,\n    MAXSIZE = UNLIMITED,\n    FILEGROWTH = 5MB\n)\nLOG ON (\n    NAME = N'MyDatabase_log',\n    FILENAME = N'C:\Databases\MyDatabase_log.ldf',\n    SIZE = 5MB,\n    MAXSIZE = UNLIMITED,\n    FILEGROWTH = 2MB\n)\nGO\n\n-- Create a schema and tables\nCREATE SCHEMA [dbo]\nGO\n\nCREATE TABLE [dbo].[MyTable] (\n    [ID] INT PRIMARY KEY IDENTITY(1,1),\n    [Name] NVARCHAR(50) NOT NULL,\n    [Description] NVARCHAR(200) NULL\n)\nGO