BEGIN\n  DBMS_SCHEDULER.CREATE_JOB (\n    job_name        => 'my_job',\n    job_type        => 'PLSQL_BLOCK',\n    job_action      => 'BEGIN DBMS_OUTPUT.PUT_LINE(''Hello World!''); END;',\n    start_date      => SYSTIMESTAMP,\n    repeat_interval => 'FREQ=HOURLY; BYHOUR=0; BYMINUTE=0;',\n    end_date        => NULL,\n    enabled         => TRUE,\n    comments        => 'My hourly job');\nEND;