DELIMITER //\n\nCREATE PROCEDURE update_dates()\nBEGIN\n  DECLARE done INT DEFAULT 0;\n  DECLARE date_val VARCHAR(255);\n  DECLARE cur1 CURSOR FOR SELECT date_column FROM your_table;\n  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;\n\n  OPEN cur1;\n\n  read_loop: LOOP\n    FETCH cur1 INTO date_val;\n    IF done THEN\n      LEAVE read_loop;\n    END IF;\n\n    UPDATE your_table\n    SET date_column = UNIX_TIMESTAMP(date_val)\n    WHERE date_column = date_val;\n  END LOOP;\n\n  CLOSE cur1;\nEND //\n\nDELIMITER ;