SELECT TO_DATE(TRAIN.DEPARTURE_DATE,'YYYYMM')\n  , (select COUNT(*) \n     FROM TRAIN t2\n     WHERE t2.PERSON_ID='28' AND t2.DEPARTURE_STATION = 'DUBLIN' AND t2.DEPARTURE_DATE = t1.DEPARTURE_DATE) AS Mark\n  , (select COUNT(*) \n     FROM TRAIN t3\n     WHERE t3.PERSON_ID='29' AND t3.DEPARTURE_STATION = 'DUBLIN' AND t3.DEPARTURE_DATE = t1.DEPARTURE_DATE) AS Chris\nFROM TRAIN t1\nGROUP BY DEPARTURE_DATE