-- Create a benchmarking procedure\nCREATE OR REPLACE PROCEDURE benchmark(p_iterations NUMBER)\nAS\n  v_start_time NUMBER;\n  v_end_time NUMBER;\nBEGIN\n  v_start_time := DBMS_UTILITY.GET_TIME();\n\n  FOR i IN 1..p_iterations LOOP\n    -- Execute the dynamic SQL statement\n    DECLARE\n      v_name VARCHAR2(50);\n    BEGIN\n      EXECUTE IMMEDIATE 'SELECT name FROM test_table WHERE id = 1' INTO v_name;\n    END;\n\n    -- Execute the stored procedure\n    EXEC test_proc(1);\n  END LOOP;\n\n  v_end_time := DBMS_UTILITY.GET_TIME();\n\n  DBMS_OUTPUT.PUT_LINE('Dynamic SQL: ' || (v_end_time - v_start_time) || ' seconds');\n  DBMS_OUTPUT.PUT_LINE('Stored Procedure: ' || (v_end_time - v_start_time) || ' seconds');\nEND;\n\n-- Run the benchmark\nEXEC benchmark(1000);