-- Get the current job settings\nSELECT name, enabled, start_step_id, \n       CASE WHEN start_step_id = 1 THEN ' Blocking' ELSE 'Non-Blocking' END AS blocking_behavior\nFROM msdb.dbo.sysjobs;\n\n-- Update the job to have non-blocking behavior (if needed)\nEXEC msdb.dbo.sp_update_job\n    @job_id = N'YourJobName',\n    @enabled = 1,\n    @start_step_id = 2;  -- Set to 2 for non-blocking