CREATE FUNCTION MySqlPasswordHash (@password nvarchar(255))\nRETURNS nvarchar(41)\nAS\nBEGIN\n    DECLARE @salt nvarchar(8) = '203A312F3A4F5850'\n    DECLARE @hashedPassword nvarchar(41)\n\n    DECLARE @sha1 varbinary(20)\n    SET @sha1 = CONVERT(varbinary(20), HASHBYTES('SHA1', CONVERT(varbinary, @salt + @password)))\n\n    DECLARE @firstHash varbinary(20)\n    SET @firstHash = CONVERT(varbinary(20), HASHBYTES('SHA1', @sha1))\n\n    SET @hashedPassword = '*' + CONVERT(nvarchar(40), @firstHash, 2)\n\n    RETURN @hashedPassword\nEND