CREATE TABLE T1 (ID INT, NAME VARCHAR(20));\nCREATE TABLE T2 (ID INT, VALUE INT);\n\nINSERT INTO T1 (ID, NAME) VALUES (1, 'John'), (2, 'Jane');\nINSERT INTO T2 (ID, VALUE) VALUES (1, 42), (2, 43);\n\nSELECT * FROM T1 JOIN T2 ON T1.ID=T2.ID WHERE T2.VALUE=42;\n\n-- returns:\n-- +----+-------+----+-------+\n-- | ID | NAME  | ID | VALUE |\n-- +----+-------+----+-------+\n-- | 1  | John  | 1  | 42    |\n-- +----+-------+----+-------+\n\nSELECT * FROM T1 JOIN T2 ON T1.ID=T2.ID AND T2.VALUE=42;\n\n-- returns:\n-- +----+-------+----+-------+\n-- | ID | NAME  | ID | VALUE |\n-- +----+-------+----+-------+\n-- | 1  | John  | 1  | 42    |\n-- +----+-------+----+-------+\n\n-- but if we add another row to T2 with ID=1 and VALUE=41\nINSERT INTO T2 (ID, VALUE) VALUES (1, 41);\n\n-- the first query will return:\n-- +----+-------+----+-------+\n-- | ID | NAME  | ID | VALUE |\n-- +----+-------+----+-------+\n-- | 1  | John  | 1  | 42    |\n-- +----+-------+----+-------+\n\n-- while the second query will return:\n-- +----+-------+----+-------+\n-- | ID | NAME  | ID | VALUE |\n-- +----+-------+----+-------+\n-- | 1  | John  | 1  | 42    |\n-- | 1  | John  | 1  | 41    |\n-- +----+-------+----+-------+