CREATE OR REPLACE PROCEDURE get_paged_data(\n  p_page_number IN INTEGER,\n  p_number_of_records_per_page IN INTEGER,\n  p_cursor OUT SYS_REFCURSOR\n)\nAS\n  v_offset INTEGER := (p_page_number - 1) * p_number_of_records_per_page;\n  v_limit INTEGER := p_number_of_records_per_page;\nBEGIN\n  OPEN p_cursor FOR\n    SELECT *\n    FROM your_table\n    ORDER BY your_column\n    OFFSET v_offset ROWS\n    FETCH NEXT v_limit ROWS ONLY;\nEND;