-- Get database users\nSELECT * FROM dbo.sysusers\n\n-- Get database roles\nSELECT * FROM dbo.sysusers WHERE issqlrole = 1\n\n-- Get user role memberships\nSELECT u.name AS UserName, r.name AS RoleName \nFROM dbo.sysmembers m\nJOIN dbo.sysusers u ON m.memberuid = u.uid\nJOIN dbo.sysusers r ON m.groupuid = r.uid\n\n-- Get object permissions\nSELECT o.name AS ObjectName, u.name AS UserName, p.permission_name AS Permission \nFROM dbo.syspermissions p\nJOIN dbo.sysobjects o ON p.id = o.id\nJOIN dbo.sysusers u ON p.grantee = u.uid