CREATE TABLE RESULTS (\n  result_id NUMBER PRIMARY KEY,\n  test_id NUMBER,\n  result_type VARCHAR2(20),\n  CONSTRAINT fk_result_type FOREIGN KEY (result_type) REFERENCES RESULT_TYPES(result_type)\n);\n\nCREATE TABLE RESULT_TYPES (\n  result_type VARCHAR2(20) PRIMARY KEY\n);\n\nCREATE TABLE INTEGER_RESULTS (\n  result_id NUMBER PRIMARY KEY,\n  raw_result NUMBER(10,0),\n  CONSTRAINT fk_integer_result FOREIGN KEY (result_id) REFERENCES RESULTS(result_id)\n);\n\nCREATE TABLE DECIMAL_RESULTS (\n  result_id NUMBER PRIMARY KEY,\n  raw_result NUMBER(10,6),\n  CONSTRAINT fk_decimal_result FOREIGN KEY (result_id) REFERENCES RESULTS(result_id)\n);