SELECT \n  'B' METRIC, \n  ROW_PRIORITY,\n  LAST_VALUE(COL1 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) COL1,\n  LAST_VALUE(COL2 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) COL2,\n  LAST_VALUE(COL3 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) COL3\nFROM \n  (SELECT * FROM ZTEST);