INSERT INTO activity_summary (sessionid, first_activity_desc, last_activity_desc)\nSELECT \n  a.sessionid,\n  (SELECT TOP 1 activity_description FROM activity_details WHERE sessionid = a.sessionid ORDER BY created ASC) AS first_activity_desc,\n  (SELECT TOP 1 activity_description FROM activity_details WHERE sessionid = a.sessionid ORDER BY created DESC) AS last_activity_desc\nFROM activity a;