-- Create a publication on the primary database\nCREATE PUBLICATION ReportingData\n    FOR DATABASE = 'YourDatabaseName'\n    WITH \n    (   @publication_type = N'Transactional',\n        @description = N'Reporting data for UK and Europe',\n        @subscriber_login = N'YourSubscriberLogin',\n        @subscriber_password = N'YourSubscriberPassword'\n    );\n\n-- Add articles (tables) to the publication\nEXEC sp_addarticle \n    @publication_name = 'ReportingData',\n    @article_name = 'YourTableName',\n    @owner = 'YourSchemaName';\n\n-- Create a subscription on the reporting server\nEXEC sp_addsubscription \n    @publication_name = 'ReportingData',\n    @subscriber_server = 'YourReportingServerName',\n    @subscriber_db = 'YourReportingDatabaseName',\n    @subscriber_login = N'YourSubscriberLogin',\n    @subscriber_password = N'YourSubscriberPassword',\n    @frequency_type = 4,  -- 4 = Run every 15 minutes\n    @frequency_interval = 15,\n    @frequency_relative_interval = 0;