SELECT \n    name AS 'Name',\n    age AS 'Age',\n    (\n        SELECT \n            Contact_Type AS 'ContactDetail/ContactType',\n            Contact_Value AS 'ContactDetail/ContactValue'\n        FROM \n            Person_Contact_Details\n        WHERE \n            id = 12\n        FOR XML PATH(''), TYPE\n    ) AS 'ContactDetails'\nFROM \n    Person_Details\nWHERE \n    id = 12\nFOR XML PATH('PersonDetails'), TYPE