USE msdb;\nGO\n\nEXEC sp_add_job @job_name=N'LSBackup_<DatabaseName>';\n\nEXEC sp_add_jobstep \n    @job_name=N'LSBackup_<DatabaseName>', \n    @step_name=N'Log Shipping Backup', \n    @subsystem=N'TSQL', \n    @command=N'BACKUP LOG [<DatabaseName>] TO DISK = ''<BackupFilePath>''', \n    @retry_attempts=0, \n    @retry_interval=0;\n\nEXEC sp_add_jobschedule \n    @job_name=N'LSBackup_<DatabaseName>', \n    @name=N'Log Shipping Backup Schedule', \n    @freq_type=4, -- daily\n    @freq_interval=1, \n    @freq_recurrence_factor=0, \n    @active_start_time=0, \n    @active_end_time=235959;\n\nEXEC sp_add_jobserver @job_name=N'LSBackup_<DatabaseName>';\nGO