-- Create a stored procedure to update inactive users\nCREATE PROCEDURE sp_UpdateInactiveUsers\nAS\nBEGIN\n    UPDATE YourTable\n    SET IsActive = 0\n    WHERE TIMESTAMPDIFF(MINUTE, LastActive, GETDATE()) > 5\nEND\nGO\n\n-- Create a new job in SQL Server Agent\nEXEC sp_add_job @job_name=N'Update Inactive Users'\n\n-- Add a step to the job to run the stored procedure\nEXEC sp_add_jobstep \n    @job_name=N'Update Inactive Users', \n    @step_name=N'Run sp_UpdateInactiveUsers', \n    @subsystem=N'TSQL', \n    @command=N'EXEC sp_UpdateInactiveUsers', \n    @retry_attempts=0, \n    @retry_interval=0\n\n-- Schedule the job to run every 5 minutes\nEXEC sp_add_jobschedule \n    @job_name=N'Update Inactive Users', \n    @name=N'Every 5 minutes', \n    @freq_type=4, -- daily\n    @freq_interval=1, \n    @freq_recurrence_factor=0, \n    @active_start_time=0, \n    @active_end_time=0, \n    @active_start_date=0, \n    @active_end_date=0, \n    @schedule_uid=N'00000000-0000-0000-0000-000000000000'