-- Create a table in the original database\nCREATE TABLE AdventureWorks.dbo.MyTable (ID INT, Name VARCHAR(50));\n\n-- Insert some data\nINSERT INTO AdventureWorks.dbo.MyTable (ID, Name) VALUES (1, 'John'), (2, 'Jane');\n\n-- Create a snapshot of the database\nCREATE DATABASE AdventureWorksSS ON (NAME = 'AdventureWorks', FILENAME = 'C:\Snapshots\AdventureWorksSS.ss');\n\n-- Delete a record from the original database\nDELETE FROM AdventureWorks.dbo.MyTable WHERE ID = 1;\n\n-- Query the snapshot\nSELECT * FROM AdventureWorksSS.dbo.MyTable;