-- Create a publication on the intranet instance\nEXEC sp_addpublication \n    @publication = 'IntranetPublication', \n    @description = 'Publication for intranet data', \n    @sync_method = 'NATIVE', \n    @repl_freq = 'CONTINUOUS', \n    @status = 'ACTIVE';\n\n-- Add the table t1 to the publication\nEXEC sp_addarticle \n    @publication = 'IntranetPublication', \n    @article = 't1', \n    @source_table = 't1', \n    @destination_table = 't1', \n    @sync_object = 't1';\n\n-- Create a subscription on the extranet instance\nEXEC sp_addsubscription \n    @publication = 'IntranetPublication', \n    @subscriber = 'ExtranetSubscriber', \n    @subscription_db = 'ExtranetDB', \n    @subscription_type = 'PULL';