declare @RowIdMin int = 10\ndeclare @RowIdMax int = 25\ndeclare @ColSort nvarchar(100) = 'MyColumn'\n\nselect * \nfrom (select Col1, Col2, \n      ROW_NUMBER() over (\n        order by \n        case @ColSort \n          when 'Col1' then Col1 \n          when 'Col2' then Col2 \n          -- add more cases for other columns\n          else Col1 -- default column\n        end desc) as RowId \n      from MyTable) dt \nwhere RowId BETWEEN @RowIdMin AND @RowIdMax