declare @RowIdMin int = 10\ndeclare @RowIdMax int = 25\ndeclare @ColSort nvarchar(100) = 'MyColumn'\n\ndeclare @sql nvarchar(max) = N'\n    select * \n    from (select Col1, Col2, ROW_NUMBER() over (order by ' + QUOTENAME(@ColSort) + ' desc) as RowId \n          from MyTable) dt \n    where RowId BETWEEN @RowIdMin AND @RowIdMax'\n\nexec sp_executesql @sql, N'@RowIdMin int, @RowIdMax int', @RowIdMin = @RowIdMin, @RowIdMax = @RowIdMax