CREATE TABLE persons (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  email VARCHAR(255)\n);\n\nCREATE TABLE relationtypes (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE person_relationships (\n  id INT PRIMARY KEY,\n  person_id INT,\n  related_person_id INT,\n  relationtype_id INT,\n  FOREIGN KEY (person_id) REFERENCES persons(id),\n  FOREIGN KEY (related_person_id) REFERENCES persons(id),\n  FOREIGN KEY (relationtype_id) REFERENCES relationtypes(id),\n  UNIQUE (person_id, related_person_id)\n);