-- Get the number of failed login attempts for a username in the last 5 minutes\nSELECT COUNT(*) FROM login_attempts\nWHERE username = 'example_username' AND attempt_time >= NOW() - INTERVAL 5 MINUTE AND successful = 0;\n\n-- Get the number of failed login attempts for an IP address in the last 5 minutes\nSELECT COUNT(*) FROM login_attempts\nWHERE ip_address = '192.168.1.100' AND attempt_time >= NOW() - INTERVAL 5 MINUTE AND successful = 0;