DECLARE @tbl_Info TABLE (Info VARCHAR(50))\n\nINSERT INTO @tbl_Info (Info)\nVALUES ('Name1'), ('Age1'), ('Sex1'), ('Name2'), ('Age2'), ('Sex2')\n\nSELECT \n    MAX(CASE WHEN RowNum % 3 = 1 THEN Info END) AS Name,\n    MAX(CASE WHEN RowNum % 3 = 2 THEN Info END) AS Age,\n    MAX(CASE WHEN RowNum % 3 = 0 THEN Info END) AS Sex\nFROM (\n    SELECT Info, \n           (SELECT COUNT(*) FROM @tbl_Info WHERE Info <= t.Info) AS RowNum\n    FROM @tbl_Info t\n) AS derived\nGROUP BY (RowNum - 1) / 3