CREATE TABLE beauty_shops (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE treatments (\n  id INT PRIMARY KEY,\n  description VARCHAR(255),\n  duration INT,\n  price DECIMAL(10, 2)\n);\n\nCREATE TABLE beauty_shop_treatments (\n  beauty_shop_id INT,\n  treatment_id INT,\n  PRIMARY KEY (beauty_shop_id, treatment_id),\n  FOREIGN KEY (beauty_shop_id) REFERENCES beauty_shops(id),\n  FOREIGN KEY (treatment_id) REFERENCES treatments(id)\n);\n\nCREATE TABLE employees (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  beauty_shop_id INT,\n  FOREIGN KEY (beauty_shop_id) REFERENCES beauty_shops(id)\n);\n\nCREATE TABLE employee_treatments (\n  employee_id INT,\n  treatment_id INT,\n  beauty_shop_treatment_id INT,\n  price DECIMAL(10, 2),\n  duration INT,\n  PRIMARY KEY (employee_id, treatment_id),\n  FOREIGN KEY (employee_id) REFERENCES employees(id),\n  FOREIGN KEY (treatment_id) REFERENCES treatments(id),\n  FOREIGN KEY (beauty_shop_treatment_id) REFERENCES beauty_shop_treatments(beauty_shop_id, treatment_id)\n);