DECLARE\n  v_cursor INTEGER;\n  v_job_id NUMBER := 123;\nBEGIN\n  v_cursor := DBMS_SQL.OPEN_CURSOR;\n  DBMS_SQL.PARSE(v_cursor, 'BEGIN APP_DB1.GET_JOB(:1, :2); END;', DBMS_SQL.NATIVE);\n  DBMS_SQL.BIND_VARIABLE(v_cursor, ':1', v_job_id);\n  DBMS_SQL.BIND_VARIABLE(v_cursor, ':2', MYGEN.sqlcur);\n  DBMS_SQL.EXECUTE(v_cursor);\n  -- Fetch and print the results\n  LOOP\n    DBMS_SQL.FETCH_ROWS(v_cursor);\n    EXIT WHEN DBMS_SQL.FETCH_STATUS = 0;\n    DBMS_OUTPUT.PUT_LINE(DBMS_SQL.COLUMN_VALUE(v_cursor, 1) || ' ' || DBMS_SQL.COLUMN_VALUE(v_cursor, 2));\n  END LOOP;\n  DBMS_SQL.CLOSE_CURSOR(v_cursor);\nEND;\n/