CREATE OR REPLACE PROCEDURE serialize_row_to_xml(\n  p_table_name IN VARCHAR2,\n  p_row_id IN ROWID,\n  p_xml OUT CLOB\n) AS\n  l_ctx DBMS_XMLGEN.ctxhandle;\n  l_xml CLOB;\nBEGIN\n  l_ctx := DBMS_XMLGEN.newcontext(p_table_name);\n  DBMS_XMLGEN.setrowsettag(l_ctx, 'ROWSET');\n  DBMS_XMLGEN.setrowtag(l_ctx, 'ROW');\n  DBMS_XMLGEN.setmaxrows(l_ctx, 1);\n  DBMS_XMLGEN.setquery(l_ctx, 'SELECT * FROM ' || p_table_name || ' WHERE ROWID = ''' || p_row_id || '''');\n  l_xml := DBMS_XMLGEN.getxml(l_ctx);\n  p_xml := l_xml;\n  DBMS_XMLGEN.closecontext(l_ctx);\nEND;\n/