CREATE VIEW vwAuctionData AS\nSELECT \n    tblAuction.article_no, \n    tblAuction.article_name, \n    tblAuction.subtitle, \n    tblAuction.current_bid, \n    tblAuction.start_price, \n    tblAuction.bid_count, \n    tblAuction.quant_total, \n    tblAuction.quant_sold, \n    tblAuction.start, \n    tblAuction.ends, \n    tblAuction.origin_end, \n    tblUser.user_name, \n    tblAuction.best_bidder_id, \n    tblAuction.finished, \n    tblAuction.watch, \n    tblAuction.buyitnow_price, \n    tblAuction.pic_url, \n    tblAuction.private_auction, \n    tblAuction.auction_type, \n    tblAuction.insert_date, \n    tblAuction.update_date, \n    tblAuction.cat_1_id, \n    tblAuction.cat_2_id, \n    tblAuction.article_desc, \n    tblAuction.countrycode, \n    tblAuction.location, \n    tblAuction.condition, \n    tblAuction.revised, \n    tblAuction.paypal_accept, \n    tblAuction.pre_terminated, \n    tblAuction.shipping_to, \n    tblAuction.fee_insertion, \n    tblAuction.fee_final, \n    tblAuction.fee_listing, \n    tblAuction.pic_xxl, \n    tblAuction.pic_diashow, \n    tblAuction.pic_count, \n    tblAuction.item_site_id\nFROM \n    tblUser \nINNER JOIN \n    tblAuction \nON \n    tblUser.id = tblAuction.seller_id;