-- Create sample tables\nCREATE TABLE A (id INT PRIMARY KEY);\nCREATE TABLE B (id INT, fk INT, FOREIGN KEY (fk) REFERENCES A(id));\n\n-- Insert sample data\nINSERT INTO A (id) VALUES (1), (2);\nINSERT INTO B (id, fk) VALUES (1, 1), (2, 2), (3, NULL);\n\n-- Query with LEFT JOIN and WHERE clause\nSELECT *\nFROM A\nLEFT JOIN B ON A.id = B.fk\nWHERE A.id = B.fk;\n\n-- Result set:\n-- id | id | fk\n-- ---+----+----\n-- 1  | 1  | 1\n-- 2  | 2  | 2\n\n-- Equivalent query with INNER JOIN\nSELECT *\nFROM A\nINNER JOIN B ON A.id = B.fk;\n\n-- Result set:\n-- id | id | fk\n-- ---+----+----\n-- 1  | 1  | 1\n-- 2  | 2  | 2