-- Get the total quantity of orders for each customer, \n-- filtered by orders placed in the last 6 months, \n-- grouped by customer name\nSELECT \n  c.customer_name, \n  SUM(o.order_quantity) AS total_quantity\nFROM \n  customers c\n  JOIN orders o ON c.customer_id = o.customer_id\nWHERE \n  o.order_date > ADD_MONTHS(SYSDATE, -6)\nGROUP BY \n  c.customer_name\nORDER BY \n  total_quantity DESC;