SELECT \n  SEQ,\n  LAST_VALUE(CASE WHEN LVL = 1 THEN DESCR END, 'IGNORE NULLS') \n    OVER (ORDER BY SEQ) AS L1,\n  LAST_VALUE(CASE WHEN LVL = 2 THEN DESCR END, 'IGNORE NULLS') \n    OVER (ORDER BY SEQ) AS L2,\n  LAST_VALUE(CASE WHEN LVL = 3 THEN DESCR END, 'IGNORE NULLS') \n    OVER (ORDER BY SEQ) AS L3\nFROM \n  TEST\nORDER BY \n  SEQ;