-- Create a stored procedure to calculate the sum\nCREATE PROCEDURE CalculateCustomerSum\n    @CustomerId INT\nAS\nBEGIN\n    SELECT SUM(OrderTotal) AS Total\n    FROM Orders\n    WHERE CustomerId = @CustomerId\nEND\n\n-- Create a caching table\nCREATE TABLE CustomerSumCache (\n    CustomerId INT PRIMARY KEY,\n    Total DECIMAL(18, 2)\n)\n\n-- Update the caching table periodically or on demand\nINSERT INTO CustomerSumCache (CustomerId, Total)\nEXEC CalculateCustomerSum @CustomerId = 1