SELECT \n    vendortofaulttypeitemsmap.vendorid, \n    vendortofaulttypeitemsmap.faultypeitemguid,\n    guid_faulttypeitems.faultypeitemname,\n    vendortoworkactionmap.workactionitemguid, \n    guid_workactionitem.workactionitemname\nFROM \n    vendortofaulttypeitemsmap\nINNER JOIN \n    guid_faulttypeitems ON\n    vendortofaulttypeitemsmap.faultypeitemguid = guid_faulttypeitems.faultypeitemguid\nINNER JOIN \n    vendortoworkactionmap ON\n    vendortofaulttypeitemsmap.vendorid = vendortoworkactionmap.vendorid\nINNER JOIN \n    guid_workactionitem ON\n    vendortoworkactionmap.workactionitemguid = guid_workactionitem.workactionitemguid\nWHERE \n    vendortofaulttypeitemsmap.vendorid = 45