CREATE TRIGGER trg_Audit_MyTable ON MyTable\nFOR INSERT, UPDATE, DELETE\nAS\nBEGIN\n    -- Log all changes\n    INSERT INTO AuditLog (TableName, ColumnName, OldValue, NewValue)\n    SELECT 'MyTable', 'MyColumn1', d.MyColumn1, i.MyColumn1\n    FROM DELETED d\n    FULL OUTER JOIN INSERTED i ON d.PrimaryKey = i.PrimaryKey\n    WHERE d.MyColumn1 <> i.MyColumn1 OR (d.MyColumn1 IS NULL AND i.MyColumn1 IS NOT NULL) OR (d.MyColumn1 IS NOT NULL AND i.MyColumn1 IS NULL)\n\n    -- Repeat for each column of interest\nEND