CREATE TABLE SpaceQuadrant (\n  ID INT PRIMARY KEY,\n  EntryName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE SpaceCoordinate (\n  ID INT PRIMARY KEY,\n  QuadrantID INT NOT NULL,\n  CoordinateData VARCHAR(255) NOT NULL,\n  FOREIGN KEY (QuadrantID) REFERENCES SpaceQuadrant(ID)\n);\n\nCREATE TABLE SpaceObject (\n  ID INT PRIMARY KEY,\n  CoordinateID INT NOT NULL,\n  ObjectData VARCHAR(255) NOT NULL,\n  FOREIGN KEY (CoordinateID) REFERENCES SpaceCoordinate(ID)\n);