SELECT i.*\nFROM IMAGES i\nJOIN (\n  SELECT 'IMG1' AS IMAGENAME, 1 AS SORT_ORDER\n  UNION ALL\n  SELECT 'IMG2', 2\n  UNION ALL\n  SELECT 'IMG3', 3\n  UNION ALL\n  SELECT 'IMG4', 4\n  UNION ALL\n  SELECT 'IMG5', 5\n  UNION ALL\n  SELECT 'IMG6', 6\n) s ON i.IMAGENAME = s.IMAGENAME\nORDER BY s.SORT_ORDER;