-- Create a new column to store the checksum of the blob data\nALTER TABLE data ADD COLUMN dt_checksum VARBINARY(32);\n\n-- Update the table to populate the checksum column\nUPDATE data SET dt_checksum = UNHEX(SHA2(dt, 256));\n\n-- Create an index on the checksum column\nCREATE INDEX idx_dt_checksum ON data (dt_checksum);\n\n-- Find duplicate blobs using the checksum index\nSELECT a.pk, MIN(b.pk) \nFROM data AS a \nJOIN data AS b \nON a.dt_checksum = b.dt_checksum \nWHERE b.pk < a.pk \nGROUP BY a.pk;