CREATE TABLE Foods (\n  food_id INT PRIMARY KEY,\n  foodName VARCHAR(50)\n);\n\nCREATE TABLE Fruits (\n  fruit_id INT PRIMARY KEY,\n  fruitName VARCHAR(50),\n  food_id INT UNIQUE NOT NULL,\n  FOREIGN KEY (food_id) REFERENCES Foods(food_id)\n);\n\nCREATE TABLE Vegetables (\n  vegetable_id INT PRIMARY KEY,\n  vegetableName VARCHAR(50),\n  food_id INT UNIQUE NOT NULL,\n  FOREIGN KEY (food_id) REFERENCES Foods(food_id)\n);\n\nCREATE TABLE favoriteFoods (\n  food_id INT,\n  FOREIGN KEY (food_id) REFERENCES Foods(food_id)\n);