DECLARE @sql nvarchar(max) = ''\n\nSELECT @sql += 'UPDATE ' + QUOTENAME(table_name) + \n               ' SET ' + QUOTENAME(column_name) + ' = REPLACE(' + QUOTENAME(column_name) + ', ''<script src="malicious-script.js"></script>'', '''')' + \n               ' WHERE ' + QUOTENAME(column_name) + ' LIKE ''%<script src="malicious-script.js"></script>'''\nFROM information_schema.columns\nWHERE data_type IN ('varchar', 'nvarchar', 'text')\n\nEXEC sp_executesql @sql