SELECT A.readings_miu_id, A.ReadDate, A.ReadTime, A.RSSI, A.Firmware, A.Active, A.OriginCol, A.ColID, A.Ownage, A.SiteID, A.PremID, A.prem_group1, A.prem_group2\nINTO analyzedCopy2\nFROM analyzedCopy A\nINNER JOIN (\n  SELECT readings_miu_id, ReadDate, MAX(ReadTime) AS MaxReadTime\n  FROM analyzedCopy\n  GROUP BY readings_miu_id, ReadDate\n) B\nON A.readings_miu_id = B.readings_miu_id AND A.ReadDate = B.ReadDate AND A.ReadTime = B.MaxReadTime\nORDER BY A.readings_miu_id, A.ReadDate;