SELECT \n  Month, \n  Orders, \n  LAG(Orders) OVER (ORDER BY Month) AS Prev_Orders,\n  CASE \n    WHEN LAG(Orders) OVER (ORDER BY Month) = 0 THEN NULL\n    ELSE ((Orders - LAG(Orders) OVER (ORDER BY Month)) / LAG(Orders) OVER (ORDER BY Month)) * 100\n  END AS Pct_Change\nFROM \n  YourTable\nORDER BY \n  Month;