DECLARE @search_string VARCHAR(50)\nSET @search_string = 'your_search_string'\n\nDECLARE @sql VARCHAR(MAX)\n\nSELECT @sql = COALESCE(@sql, '') + \n    'SELECT ''' + TABLE_NAME + '.' + COLUMN_NAME + ''' AS table_column, * \n     FROM ' + TABLE_NAME + ' \n     WHERE ' + COLUMN_NAME + ' LIKE ''' + @search_string + ''''\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE DATA_TYPE IN ('varchar', 'nvarchar', 'char', 'nchar')\n\nEXEC sp_executesql @sql