-- Insert users\nINSERT INTO users (id, firstName, surname, ...)\nSELECT id, firstName, surname, ...\nFROM customers;\n\n-- Insert addresses\nINSERT INTO addresses (id, user_id, street1, street2, town, county, postcode, country, type)\nSELECT \n  NULL,  -- id will be auto-incremented\n  id,  -- user_id\n  address1, \n  address2, \n  town, \n  county, \n  postcode, \n  country, \n  'billing'  -- type\nFROM customers\nUNION ALL\nSELECT \n  NULL,  -- id will be auto-incremented\n  id,  -- user_id\n  delAddress1, \n  delAddress2, \n  delTown, \n  delCounty, \n  delPostcode, \n  delCountry, \n  'delivery'  -- type\nFROM customers;\n\n-- Insert phone numbers\nINSERT INTO phone_numbers (id, user_id, number, type)\nSELECT \n  NULL,  -- id will be auto-incremented\n  id,  -- user_id\n  tel, \n  'home'  -- type\nFROM customers\nUNION ALL\nSELECT \n  NULL,  -- id will be auto-incremented\n  id,  -- user_id\n  mobile, \n  'mobile'  -- type\nFROM customers\nUNION ALL\nSELECT \n  NULL,  -- id will be auto-incremented\n  id,  -- user_id\n  workTel, \n  'work'  -- type\nFROM customers;